mapper.select:
SELECT USER_NO
FROM USER_INFO
WHERE USER_ID = #{userId}

mapper.insert:
INSERT INTO USER_INFO(USER_NO, USER_ID, USER_PW)
VALUES (
	USER_NO.NEXTVAL
	, #{userId}
	, #{userPw}
)

mapper.select:
SELECT USER_NO, USER_ID, USER_RL, USER_MC
FROM USER_INFO
WHERE USER_ID = #{userId}
AND USER_PW = #{userPw}
